<?php
 if ( !defined ( 'ABSPATH' ) ) { exit; } if ( !class_exists ( 'ReduxFramework_divide' ) ) { class ReduxFramework_divide { function __construct ( $field = array(), $value = '', $parent ) { $this->parent = $parent; $this->field = $field; $this->value = $value; } public function render () { echo '</td></tr></table>'; echo '<div data-id="' . $this->field[ 'id' ] . '" id="divide-' . $this->field[ 'id' ] . '" class="divide ' . $this->field[ 'class' ] . '"><div class="inner"><span>&nbsp;</span></div></div>'; echo '<table class="form-table no-border"><tbody><tr><th></th><td>'; } public function enqueue() { if ($this->parent->args['dev_mode']) { wp_enqueue_style( 'redux-field-divide', ReduxFramework::$_url . 'inc/fields/divide/field_divide.css', array(), time(), 'all' ); } } } }