<?php
 if ( ! class_exists( 'Redux_Validation_comma_numeric' ) ) { class Redux_Validation_comma_numeric { function __construct( $parent, $field, $value, $current ) { $this->parent = $parent; $this->field = $field; $this->field['msg'] = ( isset( $this->field['msg'] ) ) ? $this->field['msg'] : __( 'You must provide a comma separated list of numerical values for this option.', 'redux-framework' ); $this->value = $value; $this->current = $current; $this->validate(); } function validate() { $this->value = str_replace( ' ', '', $this->value ); if ( ! is_numeric( str_replace( ',', '', $this->value ) ) ) { $this->value = ( isset( $this->current ) ) ? $this->current : ''; $this->error = $this->field; } } } }