<?php
 if ( ! class_exists( 'Redux_Validation_css' ) ) { class Redux_Validation_css { function __construct( $parent, $field, $value, $current ) { $this->parent = $parent; $this->field = $field; $this->value = $value; $this->current = $current; $this->validate(); } function validate() { $data = $this->value; $data = wp_filter_nohtml_kses( $data ); $data = str_replace( '&gt;', '>', $data ); $data = stripslashes($data); if ($data != $this->value) { $this->field['msg'] = __( 'Unsafe strings were found in your CSS and have been filtered out.', 'redux-framework' ); $this->warning = $this->field; } $this->value = $data; } } }