<?php
 if ( ! class_exists( 'Redux_Validation_email' ) ) { class Redux_Validation_email { function __construct( $parent, $field, $value, $current ) { $this->parent = $parent; $this->field = $field; $this->field['msg'] = ( isset( $this->field['msg'] ) ) ? $this->field['msg'] : __( 'You must provide a valid email for this option.', 'redux-framework' ); $this->value = $value; $this->current = $current; $this->validate(); } function validate() { if ( ! is_email( $this->value ) ) { $this->value = ( isset( $this->current ) ) ? $this->current : ''; $this->error = $this->field; } } } }