<?php
 if ( ! class_exists( 'Redux_Validation_html' ) ) { class Redux_Validation_html { function __construct( $parent, $field, $value, $current ) { $this->parent = $parent; $this->field = $field; $this->value = $value; $this->current = $current; $this->validate(); } function validate() { $this->value = wp_kses_post( $this->value ); } } }