<?php
 if ( ! class_exists( 'Redux_Validation_no_special_chars' ) ) { class Redux_Validation_no_special_chars { function __construct( $parent, $field, $value, $current ) { $this->parent = $parent; $this->field = $field; $this->field['msg'] = ( isset( $this->field['msg'] ) ) ? $this->field['msg'] : __( 'You must not enter any special characters in this field, all special characters have been removed.', 'redux-framework' ); $this->value = $value; $this->current = $current; $this->validate(); } function validate() { if ( ! preg_match( '/[^a-zA-Z0-9_ -]/s', $this->value ) == 0 ) { $this->warning = $this->field; } $this->value = preg_replace( '/[^a-zA-Z0-9_ -]/s', '', $this->value ); } } }