<?php
 class acf_settings_info { function __construct() { add_action('admin_menu', array($this, 'admin_menu')); } function admin_menu() { if( !acf_get_setting('show_admin') ) { return; } add_submenu_page('edit.php?post_type=acf-field-group', __('Info','acf'), __('Info','acf'), acf_get_setting('capability'),'acf-settings-info', array($this,'html')); } function html() { $view = array( 'version' => acf_get_setting('version'), 'have_pro' => acf_get_setting('pro'), 'tabs' => array( 'new' => __("What's New", 'acf'), 'changelog' => __("Changelog", 'acf') ), 'active' => 'new' ); if( !empty($_GET['tab']) && array_key_exists($_GET['tab'], $view['tabs']) ) { $view['active'] = $_GET['tab']; } acf_get_view('settings-info', $view); } } new acf_settings_info(); ?>
