<?php
 if( ! class_exists('acf_field_message') ) : class acf_field_message extends acf_field { function __construct() { $this->name = 'message'; $this->label = __("Message",'acf'); $this->category = 'layout'; $this->defaults = array( 'value' => false, 'message' => '', 'esc_html' => 0 ); parent::__construct(); } function render_field( $field ) { $field['message'] = wptexturize( $field['message'] ); if( $field['esc_html'] ) { $field['message'] = esc_html( $field['message'] ); } $field['message'] = wpautop( $field['message'] ); echo $field['message']; } function render_field_settings( $field ) { acf_render_field_setting( $field, array( 'label' => __('Message','acf'), 'instructions' => __('Please note that all text will first be passed through the wp function ','acf') . '<a href="http://codex.wordpress.org/Function_Reference/wpautop" target="_blank">wpautop()</a>', 'type' => 'textarea', 'name' => 'message', )); acf_render_field_setting( $field, array( 'label' => __('Escape HTML','acf'), 'instructions' => __('Allow HTML markup to display as visible text instead of rendering','acf'), 'type' => 'radio', 'name' => 'esc_html', 'choices' => array( 1 => __("Yes",'acf'), 0 => __("No",'acf'), ), 'layout' => 'horizontal', )); } } new acf_field_message(); endif; ?>
