<?php
 if( ! class_exists('acf_field_password') ) : class acf_field_password extends acf_field { function __construct() { $this->name = 'password'; $this->label = __("Password",'acf'); $this->defaults = array( 'placeholder' => '', 'prepend' => '', 'append' => '', 'readonly' => 0, 'disabled' => 0, ); parent::__construct(); } function render_field( $field ) { $o = array( 'type', 'id', 'class', 'name', 'value', 'placeholder' ); $e = ''; if( $field['prepend'] !== "" ) { $field['class'] .= ' acf-is-prepended'; $e .= '<div class="acf-input-prepend">' . $field['prepend'] . '</div>'; } if( $field['append'] !== "" ) { $field['class'] .= ' acf-is-appended'; $e .= '<div class="acf-input-append">' . $field['append'] . '</div>'; } $atts = array(); foreach( $o as $k ) { $atts[ $k ] = $field[ $k ]; } foreach( array( 'readonly', 'disabled' ) as $k ) { if( $field[ $k ] ) { $atts[ $k ] = $k; } } $e .= '<div class="acf-input-wrap">'; $e .= '<input ' . acf_esc_attr( $atts ) . ' />'; $e .= '</div>'; echo $e; } function render_field_settings( $field ) { acf_render_field_setting( $field, array( 'label' => __('Placeholder Text','acf'), 'instructions' => __('Appears within the input','acf'), 'type' => 'text', 'name' => 'placeholder', )); acf_render_field_setting( $field, array( 'label' => __('Prepend','acf'), 'instructions' => __('Appears before the input','acf'), 'type' => 'text', 'name' => 'prepend', )); acf_render_field_setting( $field, array( 'label' => __('Append','acf'), 'instructions' => __('Appears after the input','acf'), 'type' => 'text', 'name' => 'append', )); } } new acf_field_password(); endif; ?>
